import React, { Component } from "react";
import { Dropdown,Form,Col,Container,InputGroup,Image,Button,ButtonGroup } from 'react-bootstrap';
import "./login.css";
import axios from 'axios';

import {Switch,Route, Link ,BrowserRouter,Router} from 'react-router-dom'
import ImageUploader from 'react-images-upload';
export default class Adddoc extends Component {
    constructor(props) {
        super(props);

        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onChangeFirstname = this.onChangeFirstname.bind(this);
        this.onChangeLastname = this.onChangeLastname.bind(this);
        this.onChangeEmai = this.onChangeEmai.bind(this);
        this.onChangeDegination = this.onChangeDegination.bind(this);
        this.onChangeDepartment = this.onChangeDepartment.bind(this);
        this.onChangeGender = this.onChangeGender.bind(this);
        this.onChangeNumber = this.onChangeNumber.bind(this);
        this.onChangeDob = this.onChangeDob.bind(this);
        this.onChangeCity = this.onChangeCity.bind(this);
        this.onChangeState = this.onChangeState.bind(this);
        this.onChangeSchoolten = this.onChangeSchoolten.bind(this);
        this.onChangeSchooltwelve = this.onChangeSchooltwelve.bind(this);
        this.onChangeGraduation = this.onChangeGraduation.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
          username:'',
          password: '',
          firstname: '',
          lastname: '',
          email: '',
          degination:'',
          department:'',
          gender: '',
          number: '',
          dob: '',
          city: '',
          state: '',
          schoolten: '',
          schooltwelve: '',
          graduation: ''


        }
      }
      onChangeUsername(e) {
        this.setState({
          username: e.target.value,
        })
      }
      onChangePassword(e) {
        this.setState({
          password: e.target.value,
        })
      }
      onChangeFirstname(e) {
        this.setState({
          firstname: e.target.value,
        })
      }
      onChangeLastname(e) {
        this.setState({
          lastname: e.target.value,
        })
      }
      onChangeEmai(e) {
        this.setState({
          email: e.target.value,
        })
      }
      onChangeDegination(e) {
        this.setState({
          degination: e.target.value,
        })
      }
      onChangeDepartment(e) {
        this.setState({
          department: e.target.value,
        })
      }
      onChangeGender(e) {
        this.setState({
          gender: e.target.value,
        })
      }
      onChangeNumber(e) {
        this.setState({
          number: e.target.value,
        })
      }
      onChangeDob(e) {
        this.setState({
          dob: e.target.value,
        })
      }
      onChangeCity(e) {
        this.setState({
          city: e.target.value,
        })
      }
      onChangeState(e) {
        this.setState({
          state: e.target.value,
        })
      }
      onChangeSchoolten(e) {
        this.setState({
          schoolten: e.target.value,
        })
      }
      onChangeSchooltwelve(e) {
        this.setState({
          gender: e.target.value,
        })
      }
      onChangeGraduation(e) {
        this.setState({
          number: e.target.value,
        })
      }

      onSubmit(e) {
        e.preventDefault();

        const doctors = {
          username: this.state.username,
          password: this.state.password,
          firstname: this.state.firstname,
          lastname: this.state.lastname,
          email: this.state.email,
          degination: this.state.degination,
          department: this.state.department,
          gender: this.state.gender,
          number: this.state.number,
          dob: this.state.dob,
          city: this.state.city,
          state: this.state.state,
          schoolten: this.state.schoolten,
          schooltwelve: this.state.schooltwelve,
          graduation: this.state.graduation

        }

        console.log(doctors);

        axios.post('/adddoc/add', doctors)
          .then(res => console.log(res.data));

        this.setState({
            username:'',
            password: '',
            firstname: '',
            lastname: '',
            email: '',
            degination:'',
            department:'',
            gender: '',
            number: '',
            dob: '',
            city: '',
            state: '',
            schoolten: '',
            schooltwelve: '',
            graduation: ''

        })
      }
    //onDrop(picture) {
      //  this.setState({
        //    pictures: this.state.pictures.concat(picture),
        //});
   // }
    render() {
